/* eslint-disable react/jsx-props-no-spreading */
import React, { useState } from 'react';
import './css/Contact.css';
import axios from 'axios';
import Recaptcha from 'react-google-recaptcha';
import { makeStyles } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Collapse from '@material-ui/core/Collapse';
import Dialog from '@material-ui/core/Dialog';
import Alert from '@material-ui/lab/Alert';
import TextField from '@material-ui/core/TextField';
import Slide from '@material-ui/core/Slide';
import { apiEndPoint } from '../config';
import Navbar from './Navbar/Navbar';


const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '100%',
    },
  },
  alert: {
    height: '100%',
  },
}));

const Transition = React.forwardRef((props, ref) => (
  <Slide direction="up" ref={ref} {...props} />
));

const Contact = () => {
  const classes = useStyles();

  const [form, setForm] = useState({
    name: '',
    subject: '',
    email: '',
    text: '',
  });

  const isEnabled = form.email.length > 0 && form.text.length > 0;

  const [isVerified, setIsVerified] = useState(false);

  const onChanger = () => {
    setIsVerified(true);
  };

  const submitForm = () => {
    axios.post(`${apiEndPoint}/contact/`, {
      name: form.name,
      subject: form.subject,
      email: form.email,
      text: form.text,
    });
  };

  const [openBad, setOpenBad] = React.useState(false);
  const [openGood, setOpenGood] = React.useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEnabled && isVerified) {
      submitForm();
      setOpenGood(true);
    } else {
      setOpenBad(true);
    }
  };

  const handleClose = () => {
    setOpenBad(false);
  };

  return (
    <>
      <Navbar />
      <div
        id="contactBody"
        style={
          {
            backgroundImage: `center center fixed no-repeat url("/Pictures/backgroundRegister.jpg")`
          }
        }
      >
        <div id="contact">
          <h1><strong>Contactez-nous</strong></h1>
          <h2>
          Vous souhaitez nous poser une question ?
          Ou tout simplement être mis en relation avec nos équipes ?
            <br />
          Veuillez compléter le formulaire ci-dessous et nous vous
          recontacterons au plus vite.
          </h2>
          <div id="obl">
            <p>* Champs obligatoires</p>
          </div>
          <form className={classes.root} noValidate autoComplete="off">
            <TextField
              id="standard-basic"
              label="nom"
              variant="standard"
              value={form.name}
              onChange={(event) => setForm({ ...form, name: event.target.value })}
            />
            <TextField
              id="standard-basic"
              label="objet"
              variant="standard"
              value={form.subject}
              onChange={(event) => setForm({ ...form, subject: event.target.value })}
            />
            <TextField
              id="standard-basic"
              label="email"
              required="required"
              variant="standard"
              value={form.email}
              onChange={(event) => setForm({ ...form, email: event.target.value })}
            />
            <TextField
              id="standard-multiline-static"
              label="message"
              required="required"
              multiline
              rows="7"
              variant="standard"
              value={form.text}
              onChange={(event) => setForm({ ...form, text: event.target.value })}
            />
          </form>
          <div id="captcha">
            <Recaptcha
              sitekey="6LenRsYUAAAAAOj8qWl5Rp66bucQCNtOKczLvviT"
              render="explicit"
              onChange={onChanger}
            />
          </div>
          <button id="button" type="submit" className="btn btn-primary" onClick={handleSubmit}>Envoyer </button>
          <div id="msgSend">
            <Collapse in={openGood}>
              <Alert
                action={
                  (
                    <IconButton
                      aria-label="close"
                      color="inherit"
                      size="small"
                      onClick={() => {
                        setOpenGood(false);
                      }}
                    >
                      <CloseIcon fontSize="inherit" />
                    </IconButton>
                  )
                }
              >
                Message envoyé !
              </Alert>
            </Collapse>
          </div>
          <Dialog
            open={openBad}
            TransitionComponent={Transition}
            keepMounted
            onClose={handleClose}
            aria-labelledby="alert-dialog-slide-title"
            aria-describedby="alert-dialog-slide-description"
            onClick={handleClose}
          >
            <Alert severity="error">Vous devez remplir les champs obligatoires</Alert>
          </Dialog>
        </div>
      </div>
    </>
  );
};

export default Contact;
