import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import '../css/PrevAdvert.css';
import { apiEndPoint } from '../../config';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';

const PrevAdvert = (props) => {

  const[img, setImg] = useState("");

  useEffect(() => {
    if(props.image){
      fetch(`${apiEndPoint}/image/${props.image}`)
      .then((response, err) => {
        if (err) {
          throw err;
        } else {
          setImg(response.url);
        }
      });
    }else {
      fetch(`${apiEndPoint}/image/default.jpg`)
      .then((response, err) => {
        if (err) {
          throw err;
        } else {
          setImg(response.url);
        }
      });
    }
  }, []);

  return (  
    <Link target='_blank' rel = "noopener noreferrer" style={{ textDecoration: 'inherit' }} to={{
      pathname: `/advert/${props.id}`
    }}>    
      <Card id="prevAdvert">
        <div id="prev-img">
          <CardMedia
            component="img"
            alt="Contemplative Reptile"
            height="150"
            width="150"
            image={img}
            title="Contemplative Reptile"
          />
        </div>
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {props.title}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            {props.describ.substring(0, 100)}...
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            {props.location}
          </Typography>
            {props.price > 0 ? 
            <Typography variant="body2" color="textSecondary" component="p">
              {props.price}
            </Typography> : null}
        </CardContent>
      </Card>
    </Link>
  )
}

export default PrevAdvert;
