import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import '../css/PrevAdvert.css';
import { apiEndPoint } from '../../config';

const PrevAdvert = (props) => {
  const[img, setImg] = useState("");

  useEffect(() => {
    if(props.image){
      fetch(`${apiEndPoint}/image/${props.image}`)
      .then((response, err) => {
        if (err) {
          throw err;
        } else {
          setImg(response.url);
        }
      });
    }else {
      fetch(`${apiEndPoint}/image/default.jpg`)
      .then((response, err) => {
        if (err) {
          throw err;
        } else {
          setImg(response.url);
        }
      });
    }
  }, []);

  return (  
    <Link target='_blank' rel = "noopener noreferrer" style={{ textDecoration: 'inherit' }} to={{
      pathname: `/advert/${props.id}`
    }}>      
      <div id="prev-advert">
        <div className="card mt-3">
          <div className="card-body">
            <img className="card-img-top" src={img} alt="Image advert" />
            <h5 className="card-title">{props.title}</h5>
            <p className="card-text">{props.describ.substring(0, 100)}...</p>
            {props.price > 0 ? <p className="card-text">{props.price} Euros</p> : null}
            <p className="card-text">{props.location}</p>
          </div>
        </div>
      </div>
    </Link>
  )
}

export default PrevAdvert;
