import React, {useState} from 'react';
import {Link} from "react-router-dom";
import '../css/Navbar.css';


const HomeNavbar = () => {

  const [bcolor, setBcolor] = useState("transparent");
  const [tcolor, setTcolor] = useState("white");

  const scrollFunction = () => {
    if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
     return setBcolor("WhiteSmoke ") & setTcolor("inherit")
    } else {
      return setBcolor("transparent") & setTcolor("white")
    }
  };   

  window.onscroll = () => { scrollFunction()};

  return (
    <nav id="homeNavbar" 
    className="navbar navbar-expand-md navbar-custom  sticky-top scrolling-navbar" 
    style={{backgroundColor : bcolor, transitionDuration: "1.5s"}}>
      <div id="navLogo">
        <Link className="nav-item nav-link active" to="/" style={{color: tcolor, transitionDuration: "1s"}}>
          <img id ="logo" src={`${process.env.PUBLIC_URL}/Pictures/logo.png`} alt='logo'/>
          Faire Garder son cheval <span className="sr-only">(current)</span>
        </Link> 
      </div>
      <div id="navMenu">
        <button 
        className="navbar-toggler" 
        type="button" 
        data-toggle="collapse" 
        data-target="#navbarNavAltMarkup" 
        aria-controls="navbarNavAltMarkup" 
        aria-expanded="false" 
        aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
          <div className="navbar-nav">
            <Link className="nav-item nav-link" to="/advertsPage" style={{color: tcolor, transitionDuration: "1s"}}>
              Annonces 
            </Link>
            <Link className="nav-item nav-link" to="/registerAdverts" style={{color: tcolor, transitionDuration: "1s"}}>
              DÃ©poser une annonce 
            </Link>
            <Link className="nav-item nav-link" to="/contact" style={{color: tcolor, transitionDuration: "1s"}}>
              Contact 
            </Link>
            <Link className="nav-item nav-link" to="/adminPage" style={{color: tcolor, transitionDuration: "1s"}}>
              Admin
            </Link>
          </div>
        </div>
      </div>
    </nav>
  );
}


export default HomeNavbar;
