/* eslint-disable react/no-unescaped-entities */

import React, { useContext } from 'react';
import {
  Map, Marker, Popup, TileLayer,
} from 'react-leaflet';
import './css/Map.css';
import { Link } from 'react-router-dom';
import { CtxAdverts } from './CtxAdverts';

const position = [47.0833, 2.4];

const CardMap = (props) => {
  const filtre = props;
  const [adverts] = useContext(CtxAdverts);

  return (
    <Map
      center={filtre.latitude && filtre.longitude
        ? [filtre.latitude, filtre.longitude]
        : position}
      zoom={filtre.latitude && filtre.longitude ? 12 : 5}
    >
      <TileLayer
        url="https://api.mapbox.com/styles/v1/mapbox/streets-v11/tiles/{z}/{x}/{y}?access_token=pk.eyJ1IjoiaGVsZW5hNDQiLCJhIjoiY2s1Z2g3Y3p1MDc2aDNsbnh2dTN4cnR2ZyJ9.vBpFX3t68o6CjQjscyNgPg"
        attribution="&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"
      />

      {filtre.handleFilters(adverts).map((advert) => (
        <Marker position={[advert.latitude, advert.longitude]}>
          <Popup>
            <h4>
              {advert.title}
            </h4>
            <br />
            <Link
              target="_blank"
              rel="noopener noreferrer"
              style={{ textDecoration: 'inherit' }}
              to={{
                pathname: `/advert/${advert.id}`,
              }}
            >
              <button type="button">voir l'annonce</button>
            </Link>
          </Popup>
        </Marker>
      ))}
    </Map>
  );
};

export default CardMap;
