import React, {Fragment, useContext, useEffect} from 'react';
import '../css/AdminPage.css';
import Navbar from '../Navbar/Navbar';
import AdvertsList from '../Adverts/AdvertsList';
import CtxAdmin from '../CtxAdmin';
import {Redirect, Link} from 'react-router-dom';

const AdminPage = () => {

  const [admin, setAdmin] = useContext(CtxAdmin);

  const disconnect = () => {
    localStorage.removeItem('token');
    window.location.reload(false);
  }

	return (
    <Fragment>
      {!admin.name && <Redirect to='/adminLogin'/>}
      <Navbar/>
      <div id="adminPage">
        <p>Admin : {admin.name}</p>
        <label> Numéro de l'annonce:
          <input type="text" name="Numéro de l'annonce" />
        </label>
      </div>
      <button onClick={disconnect} id="disconect" type="button" className="btn btn-primary">

      <Link to="/adminLogin">
        Deconnexion
      </Link>
      </button>

    </Fragment>
  );
}

export default AdminPage;
