/* eslint-disable react/jsx-props-no-spreading */
/* eslint-disable react/no-unescaped-entities */
/* eslint-disable no-nested-ternary */

import React, { useState, useEffect } from 'react';
import { apiEndPoint } from '../config';
import axios from 'axios';
import Recaptcha from 'react-google-recaptcha';
import Navbar from './Navbar/Navbar';
import { faCrosshairs, faDownload } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Alert from '@material-ui/lab/Alert';
import Autocomplete from '@material-ui/lab/Autocomplete';
import CloseIcon from '@material-ui/icons/Close';
import Collapse from '@material-ui/core/Collapse';
import Dialog from '@material-ui/core/Dialog';
import FormControl from '@material-ui/core/FormControl';
import ListSubheader from '@material-ui/core/ListSubheader';
import { makeStyles } from '@material-ui/core/styles';
import MenuItem from '@material-ui/core/MenuItem';
import IconButton from '@material-ui/core/IconButton';
import InputLabel from '@material-ui/core/InputLabel';
import Select from '@material-ui/core/Select';
import Slide from '@material-ui/core/Slide';
import TextField from '@material-ui/core/TextField';
import $ from 'jquery';
import './css/RegisterAdverts.css';

// --------CONFIG MATERIAL-UI----------//

const useStyles = makeStyles(theme => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: 100,
      '& > *': {
        margin: theme.spacing(1),
        width: '100%',
      },
    },
    alert: {
      height: '100%',
    },
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

const Transition = React.forwardRef((props, ref) => (
  <Slide direction="up" ref={ref} {...props} />
));

// --------COMPOSANT RegisterAdverts------//
const RegisterAdverts = () => {
  const classes = useStyles();

  // ---------------TOASTER SUCCESS/FAIL------------------//

  const [openBad, setOpenBad] = React.useState(false);
  const [openGood, setOpenGood] = React.useState(false);
  const handleClose = () => {
    setOpenBad(false);
  };

  // --------POST vers API--------- //

  const [statusServeur, setStatusServeur] = useState(true);
  const [form, setForm] = useState({
    code: '',
    latitude: '',
    longitude: '',
    name: '',
    email: '',
    phone: undefined,
    title: '',
    location: '',
    price: undefined,
    description: '',
    types: 1,
    category: '',
    picture: null,
    leurre: '',
  });

  const submitForm = () => {
    const formData = new FormData();

    formData.append('types', form.types);
    formData.append('name', form.name);
    formData.append('email', form.email);
    formData.append('phone', form.phone);
    formData.append('location', form.location);
    formData.append('title', form.title);
    formData.append('price', form.price);
    formData.append('description', form.description);
    formData.append('category_id', form.category);
    formData.append('picture', form.picture);
    formData.append('latitude', form.latitude);
    formData.append('longitude', form.longitude);

    const config = {
      headers: {
        'content-type': 'multipart/form-data',
      },
    };

    axios.post(`${apiEndPoint}/advert`, formData, config)
      .then(() => {
        const handleRedirection = () => {
          window.location = '/advertsPage';
        };
        setStatusServeur(statusServeur);
        setOpenGood(true);
        setTimeout(handleRedirection, 5000);
        console.log(form);
      })
      .catch((error) => {
        if (error) {
          setOpenBad(true);
        }
      });
  };

  useEffect(() => {
    console.log(form);
  }, [form]);

  // ---------------RECUPERER LES DIFFERENTES CATEGORIES D'ANNONCES------------------//

  // ---------------Config Material-ui pour les catégories (visuel)------------------//
  const inputLabel = React.useRef(null);
  const [labelWidth, setLabelWidth] = React.useState(0);
  const SampleComponent = () => { //ne pas supprimer, sert à donner le format pour le select des 'catégories à choisir'
    useEffect(() => {
      setLabelWidth(inputLabel.current.offsetWidth);
    }, []);
  };
  // ---------------axios des différentes catégories------------------//
  const [services, setServices] = useState([]);
  const [pensions, setPensions] = useState([]);
  const [activ, setActiv] = useState([]);
  const [ventes, setVentes] = useState([]);

  useEffect(() => {
    axios.get(`${apiEndPoint}/category/echanges`)
      .then((result) => {
        setServices(result.data);
      });
  }, []);

  useEffect(() => {
    axios.get(`${apiEndPoint}/category/pensions`)
      .then((result) => {
        setPensions(result.data);
      });
  }, []);

  useEffect(() => {
    axios.get(`${apiEndPoint}/category/activites`)
      .then((result) => {
        setActiv(result.data);
      });
  }, []);

  useEffect(() => {
    axios.get(`${apiEndPoint}/category/ventes`)
      .then((result) => {
        setVentes(result.data);
      });
  }, []);

  // ---------------PREVIEW PICTURE DOWNLOAD (jquery)------------------//

  const readURL = (input) => {
    if (input.files && input.files[0]) {
      const reader = new FileReader();
      reader.onload = function (e) {
        $('#blah').attr('src', e.target.result);
      };
      reader.readAsDataURL(input.files[0]);
    }
  };
  $('#imgInp').change(function () {
    readURL(this);
  });

  // ----------------LABELS OBLIGATOIRES------------------//

  const [cocheLegalNotices, setCocheLegalNotices] = useState(false);

  const isEnabled = form.category !== 'Choisir une catégorie *'
    && form.category !== ''
    && form.title.length > 0
    && form.description.length > 0
    && form.location.length > 0
    && form.email.length > 0;

  // ---------------RECAPTCHA----------------//

  const [isVerified, setIsVerified] = useState(false);

  const onChanger = () => {
    setIsVerified(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEnabled && isVerified && cocheLegalNotices) {
      submitForm();
    } else {
      setOpenBad(true);
    }
  };

  // ---------------BUTTON 'LOCALISEZ-MOI' ----------------//

  const handleButton = (e) => {
    e.preventDefault();
    navigator.geolocation.getCurrentPosition((position) => {
      const lat = position.coords.latitude;
      const long = position.coords.longitude;
      axios.get(`https://geo.api.gouv.fr/communes?lat=${position.coords.latitude}&lon=${position.coords.longitude}&fields=nom,departement,codesPostaux`)
        .then((response) => {
          const codes = response.data[0].codesPostaux;
          const loc = `${Math.min(...codes)} ${response.data[0].nom} - ${response.data[0].departement.nom}`;
          setForm({
            ...form, location: loc, latitude: lat, longitude: long,
          });
        });
    });
  };

  // ---------------AUTOCOMPLETION----------------//

  const [newCities, setNewCities] = useState([]);

  const handleNewCities = (e) => {
    setForm({
      ...form, location: e.currentTarget.textContent, leurre: e.currentTarget.textContent,
    });
    axios.get(`https://geo.api.gouv.fr/communes?nom=${e.target.value}&fields=nom,departement,codesPostaux,centre&boost=population&limit=20`)
      .then((response) => {
        if (response.data.length > 0) {
          let cities = [];
          response.data.forEach((city) => {
            if (city.departement) {
              const codes = city.codesPostaux;
              cities = [...cities, `${Math.min(...codes)} ${city.nom} - ${city.departement.nom}`];
            } else {
              cities = [...cities, city.nom];
            }
          });
          setNewCities(cities);
        } else {
          setNewCities([]);
        }
      });
  };

  useEffect(() => {
    setForm({ ...form, latitude: '', longitude: '' });
    if (form.location.split(' ').length > 1 && form.latitude === '') {
      const code = form.location.split(' ')[0];
      axios.get(`https://geo.api.gouv.fr/communes?codePostal=${code}&fields=centre`)
        .then((response) => {
          const lat = response.data[0].centre.coordinates[1];
          const long = response.data[0].centre.coordinates[0];
          setForm({ ...form, latitude: lat, longitude: long });
        });
    }
  }, [form.leurre]);

  // ---------------RETURN----------------//
  return (
    <>
      <Navbar />
      <div
        id="registerAdverts"
        style={
          {
            backgroundImage: `url("${process.env.PUBLIC_URL}/Pictures/backgroundRegister.jpg")`,
            backgroundPosition: `center`,
            backgroundAttachment: `fixed`,
            backgroundRepeat: `no-repeat`,
            backgroundSize: `100%`
          }
        }>
        <div className="form-style-5">
          <h1>Déposer une annonce</h1>
          <form id="form1" runat="server">
            <p>* Champs obligatoires</p>
            <div id="containerForm">
              <fieldset>
                <legend>
                  <span className="number">1</span>
                  Description
              </legend>
                <div id="catAnnonce">
                  <label htmlFor="offre">
                    Je propose
                  <input className={!form.types ? '' : 'ok'} onChange={() => setForm({ ...form, types: 1 })} type="radio" name="offre" id="offre" value={form.types} checked={form.types} />
                  </label>
                  <label htmlFor="demande">
                    Je recherche
                  <input className={!form.types ? '' : 'ok'} onChange={() => setForm({ ...form, types: 0 })} type="radio" name="demande" id="demande" value={form.types} checked={!form.types} />
                  </label>
                </div>
                <FormControl className={classes.formControl}>
                  <InputLabel id="demo-simple-select-label">Choisir une catégorie *</InputLabel>
                  <Select
                    labelId="demo-simple-select-label"
                    required="required"
                    id="demo-simple-select"
                    onChange={(event) => {
                      setForm({ ...form, category: event.target.value });
                    }}
                  >
                    <ListSubheader>Echanges de services</ListSubheader>
                    {services.map((category) => (
                      <MenuItem value={category.id} key={category.name}>{category.name}</MenuItem>
                    ))};

                    <ListSubheader>Pensions</ListSubheader>
                    {pensions.map((category) => (
                      <MenuItem value={category.id} key={category.name}>{category.name}</MenuItem>
                    ))};

                    <ListSubheader>Activités et balades</ListSubheader>
                    {activ.map((category) => (
                      <MenuItem value={category.id} key={category.name}>{category.name}</MenuItem>
                    ))}
                    ;

                    <ListSubheader>Ventes/Dons/Locations</ListSubheader>
                    {ventes.map((category) => (
                      <MenuItem value={category.id} key={category.name}>{category.name}</MenuItem>
                    ))}
                    ;

                  </Select>
                </FormControl>

                <TextField
                  id="standard-basic"
                  label="Titre"
                  required="required"
                  variant="standard"
                  value={form.title}
                  required="required"
                  maxLength="100"
                  onChange={(event) => setForm({ ...form, title: event.target.value })}
                />
                <TextField
                  id="standard-textarea"
                  multiline
                  rowsMax="7"
                  label="Description"
                  required="required"
                  type="text"
                  variant="standard"
                  value={form.description}
                  required="required"
                  maxLength="1200"
                  onChange={(event) => setForm({ ...form, description: event.target.value })}
                  className={classes.root} noValidate autoComplete="off"
                />
                <p id="describeLength">
                  caractères restants :
                {1200 - form.description.length}
                  / 1200
                </p>
                <TextField
                  id="standard-basic"
                  label="Prix"
                  type="number"
                  value={form.price}
                  maxLength="10"
                  onChange={(event) => setForm({ ...form, price: event.target.value })}
                />
              </fieldset>
              <legend>
                <span className="number">2</span>
                Télécharger une image
            </legend>
              <fieldset>
                <label className="downloadImg" htmlFor="imgInp">
                  Télécharger une image
                <input
                    className={!form.picture ? '' : 'ok'}
                    onChange={(event) => setForm({ ...form, picture: event.target.files[0] })}
                    type="file"
                    placeholder="Télécharger une image"
                    name="picture"
                    maxLength="100"
                    id="imgInp"
                    style={{ display: 'none' }}
                  />
                </label>
                <button
                  onClick={(e) => setForm({ ...form, picture: null }, e.preventDefault())}
                  className="button_register"
                  type="button"
                  id="ImgDelete"
                >
                  Supprimer l'image
              </button>
                <img id="blah" src="#" alt="fairegardersoncheval" className={!form.picture ? 'imgUnloaded' : 'imgLoaded'} />
              </fieldset>
              <fieldset>
                <legend>
                  <span className="number">3</span>
                  Localisation
              </legend>
                <Autocomplete
                  onChange={handleNewCities}
                  options={newCities}
                  className={!form.location ? '' : 'ok'}
                  value={form.location}
                  renderInput={(params) => (
                    <TextField
                      {...params}
                      onChange={handleNewCities}
                      label="Localisation *"
                      className={!form.location ? '' : 'ok'}
                      placeholder="Entrer votre ville *"
                      margin="normal"
                      variant="standard"
                      id="standard-basic"
                    />
                  )}
                />
                <button type="submit" id="localize" onClick={handleButton}>
                  <FontAwesomeIcon
                    icon={faCrosshairs}
                  />
                  Localisez moi !
              </button>
              </fieldset>
              <fieldset>
                <legend>
                  <span className="number">4</span>
                  Contact
              </legend>
                <TextField
                  id="standard-basic"
                  label="Nom"
                  variant="standard"
                  value={form.name}
                  onChange={(event) => setForm({ ...form, name: event.target.value })}
                />
                <TextField
                  id="standard-basic"
                  label="Email"
                  required="required"
                  variant="standard"
                  value={form.email}
                  maxLength="50"
                  onChange={(event) => setForm({ ...form, email: event.target.value })}
                />
                <TextField
                  id="standard-basic"
                  label="Téléphone"
                  type="tel"
                  variant="standard"
                  value={form.phone}
                  maxLength="10"
                  name="phone"
                  onChange={(event) => setForm({ ...form, phone: event.target.value })}
                />
                <legend>
                  <span className="number">5</span>
                  Déposer l'annonce
              </legend>
                <p>
                  <a
                    target="_blank"
                    rel="noopener noreferrer"
                    href="https://www.ifce.fr/wp-content/uploads/2019/09/Pret_a_usage__2_.pdf"
                  >
                    <FontAwesomeIcon icon={faDownload} id="iconDownload" />
                    Télécharger un exemple de contrat IFCE
                </a>
                </p>
                <div id="legalNotices">
                  <input
                    onChange={() => setCocheLegalNotices(!cocheLegalNotices)}
                    type="checkbox"
                    name="inputLegalNotices"
                    id="inputLegalNotices"
                    value={cocheLegalNotices}
                  />
                  <p>
                    J'accepte les
                  <a href="LegalNotices" rel="noopener noreferrer" target="_blank"> mentions légales</a>
                  </p>
                </div>
              </fieldset>
              <Recaptcha
                sitekey="6LenRsYUAAAAAOj8qWl5Rp66bucQCNtOKczLvviT"
                render="explicit"
                onChange={onChanger}
              />
              <button
                type="submit"
                onClick={handleSubmit}
                id="button_register"
                href="#"
              >
                Envoyer le formulaire
            </button>
              <div id="msgSend">
                <Collapse in={openGood}>
                  <Alert
                    action={
                      (
                        <IconButton
                          aria-label="close"
                          color="inherit"
                          size="small"
                          onClick={() => {
                            setOpenGood(false);
                          }}
                        >
                          <CloseIcon fontSize="inherit" />
                        </IconButton>
                      )
                    }
                  >
                  </Alert>
                </Collapse>
              </div>
              Annonce postée avec succès ! Vous allez être redirigé vers la page des annonces .
              <Dialog
                open={openBad}
                TransitionComponent={Transition}
                keepMounted
                onClose={handleClose}
                aria-labelledby="alert-dialog-slide-title"
                aria-describedby="alert-dialog-slide-description"
                onClick={handleClose}
              >
                <Alert severity="error">Vous devez remplir les champs obligatoires</Alert>
              </Dialog>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};
export default RegisterAdverts;
