import React, { useEffect, useState } from 'react';
importÂ { apiEndPoint } from '../config';
import axios from 'axios';
import './css/Gallery.css'

const Gallery = () => {

  const [gallery, setGallery] = useState([]);
  const [title, setTitle] = useState([]);

  useEffect(() => {
    axios.get(`${apiEndPoint}/gallery/`)
      .then((result) => {
        result.data.forEach((item) => {
          setGallery({
            title: [...title, item.data[0].title],
          });
        })
        console.log(result.data);
      });
  }, []);

/* --------- GET ELEMENTS FROM TABLE GALLERY----- */

 return (
   <div id="gallery">
     <h1>Welcome to the gallery</h1>
     <h2>Admire pictures of the past shows took by our spectators</h2>
     <div>
     {
          gallery.map((picture, index) => {
            return ((index),
              <div id="structure">
                   <div id="title">
                  <p>{picture.title[index]}</p>
                </div>
              </div>
            )
          })
        }
     </div>
   </div>
 )
}

export default Gallery;
