import React, { useState, useEffect, Fragment, useContext } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelope } from '@fortawesome/free-solid-svg-icons';
import Navbar from '../Navbar/Navbar';
import { CtxAdverts } from '../CtxAdverts';
import { apiEndPoint } from '../../config';
import '../css/Advert.css';

const Advert = (props) => {
  const [advertId, setAdvertId] = useState(null);
  const [adverts, setAdverts] = useContext(CtxAdverts);

  const[img, setImg] = useState("");

  const print = () => {
    return window.print();
  }

  useEffect(() => {
    if(props.location.pathname.split('/')[3]){
      fetch(`${apiEndPoint}/image/${props.location.pathname.split('/')[3]}`)
      .then((response, err) => {
        if (err) {
          throw err;
        } else {
          setImg(response.url);
        }
      });
    }else {
      fetch(`${apiEndPoint}/image/default.jpg`)
      .then((response, err) => {
        if (err) {
          throw err;
        } else {
          setImg(response.url);
        }
      });
    }
    setAdvertId(parseInt(props.match.params.id) - 1);
  }, []);

  return (
    <Fragment>
      <Navbar />
        <div id="advert">
          {adverts.length > 0 ?
            <div className="card mt-3">
              <p>Annonce n°{adverts[advertId].id}</p>
              <div className="card-body">
                <h5 className="card-title">{adverts[advertId].title}</h5>
                <img class="card-img-top" src={img} alt="Image advert" />
                <p className="card-text">{adverts[advertId].description}</p>
                {adverts[advertId].price > 0 ? <p className="card-text">{adverts[advertId].price} Euros</p> : null}
                {adverts[advertId].phone > 0 ? <p className="card-text">Tel: {adverts[advertId].phone}</p> : null}
                <p className="card-text">{adverts[advertId].location}</p>
                <div>
                  <a href={"mailto:" + adverts[advertId].email}> <button className="btn btn-success m-1">Contacter {adverts[advertId].name} <FontAwesomeIcon icon={faEnvelope} /></button></a>
                  <button id="propose" type="button" className="btn btn-primary" onClick={print}> Imprimer</button> 
                </div>
              </div>
            </div> : <p>Loading...</p>
          }
        </div>
    </Fragment>
  );
}

export default Advert;
