import React, { useState, useEffect, Fragment, useContext } from 'react';
import { CtxAdverts } from '../CtxAdverts';
import PrevAdvert from './PrevAdvert';
import '../css/AdvertsList.css';

const AdvertsList = (props) => {
  const [adverts] = useContext(CtxAdverts);

    const list = () =>{
      return (
        props.handleFilters(adverts).map((advert, index) => {
          return <PrevAdvert 
        key={index} 
        title={advert.title} 
        describ={advert.description}
        id={advert.id}
        types={advert.types}
        location={advert.location}
        price={advert.price}
        name={advert.name}
        image={advert.url_image}
        email={advert.email}  />
        })
      )
    }

	return (
    <Fragment>
      <div id="advertList">
        {list().length<=0 ? <p>Aucune annonce ne correspond Ã  votre recherche</p> : list()}
      </div>
    </Fragment>

  );
}

export default AdvertsList;
