import React, { useState, useEffect, Fragment, useContext } from 'react';
import CardMap from '../CardMap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCrosshairs, faSearch } from '@fortawesome/free-solid-svg-icons';
import AdvertsList from './AdvertsList';
import axios from 'axios';
import '../css/AdvertsPage.css';
import Navbar from '../Navbar/Navbar';
import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { CtxAdverts } from '../CtxAdverts';
import { apiEndPoint } from '../../config';

const AdvertsPage = () => {
  const [adverts] = useContext(CtxAdverts);
  const [filters, setFilters] = useState([{ types: 1, location: "", keywords: "", category: null }]);
  const [newCities, setNewCities] = useState([]);
  const [services, setServices] = useState([]);
  const [pensions, setPensions] = useState([]);
  const [activites, setActivites] = useState([]);
  const [ventes, setVentes] = useState([]);
  const [types, setTypes] = useState(1);
	const [category, setCategory] = useState(null);
	const [keyWord, setKeyWord] = useState("");
  const [location, setLocation] = useState("");
  const [latitude, setLatitude] = useState(0);
  const [longitude, setLongitude] = useState(0);

  useEffect(() => {
		axios.get(`${apiEndPoint}/category/echanges`)
			.then(result => {
				setServices(result.data)
			})
  }, [])

  useEffect(() => {
		axios.get(`${apiEndPoint}/category/pensions`)
			.then(result => {
				setPensions(result.data)
			})
  }, [])

  useEffect(() => {
		axios.get(`${apiEndPoint}/category/activites`)
			.then(result => {
				setActivites(result.data)
			})
  }, [])

  useEffect(() => {
		axios.get(`${apiEndPoint}/category/ventes`)
			.then(result => {
				setVentes(result.data)
			})
	}, [])

	const handleType = () => {
		if (types === 1) {
      setFilters({...filters, types: 0});
			setTypes(0);
		} else {
      setFilters({...filters, types: 1});
			setTypes(1);
		}
	}
	const handleCategory = (e) => {
    setFilters({...filters, category: Number(e.target.value)});
		setCategory(Number(e.target.value));
	}
	const handleKeyWord = (e) => {
    setFilters({...filters, keywords: e.target.value});
		setKeyWord(e.target.value);
	}

  // ---------------Bouton Geolocalisation----------------//
  const handleLocation = (e) => {
    e.preventDefault();
    navigator.geolocation.getCurrentPosition((position) => {
      setLatitude(position.coords.latitude);
      setLongitude(position.coords.longitude);
      axios.get(`https://geo.api.gouv.fr/communes?lat=${position.coords.latitude}&lon=${position.coords.longitude}&fields=nom,departement,codesPostaux`)
        .then((response) => {
          const codes = response.data[0].codesPostaux
          const loc = `${Math.min(...codes)} ${response.data[0].nom} - ${response.data[0].departement.nom}`;
          setFilters({...filters, location: loc});
          setLocation(loc);

        });
    });
  };

  // ---------------AUTOCOMPLETION----------------//
	const handleNewCities = (e) => {
    setFilters({...filters, location: e.currentTarget.textContent});
    setLocation(e.currentTarget.textContent);
    axios.get(`https://geo.api.gouv.fr/communes?nom=${e.target.value}&fields=nom,departement,codesPostaux,centre&boost=population&limit=20`)
      .then(response => {
        if (response.data.length > 0) {
          let cities = [];
          response.data.forEach((city) => {
            if (city.departement) {
              const codes = city.codesPostaux
              cities = [...cities, Math.min(...codes) + ' ' + city.nom + ' - ' + city.departement.nom]
            } else {
              cities = [...cities, city.nom]
            };
          });
          setNewCities(cities);
        } else {
          setNewCities([]);
        }
      });
  };

  // ---------------Récuperation coordonées après autocompletion----------------//
  useEffect(() => {
    setLatitude(''); 
    setLongitude('');
    if(location.split(' ').length > 1 && latitude === ''){
      const code = location.split(' ')[0]
      axios.get(`https://geo.api.gouv.fr/communes?codePostal=${code}&fields=centre`)
        .then(response => {
          const lat = response.data[0].centre.coordinates[1];
          const long = response.data[0].centre.coordinates[0];
          setLatitude(lat);
          setLongitude(long);
        })
    }
  }, [location]);

// ---------------Filtres----------------//
  const handleFilters = () =>{
    return adverts.filter((advert) => {
      if(category){
        return advert.category_id === category
      }else{
        return advert
      }})
      .filter((advert) => {
        return advert.types === types})
      .filter((advert) => {
        return advert.title.toLowerCase().includes(keyWord.toLowerCase())})
      .filter((advert) => {
        return advert.location.toLowerCase().includes(location.toLowerCase())})
      }

	return (
    <Fragment> 
      <Navbar/>

      <div id="advertsPage">
        <form>
          <div className="radio-button">
            <label>
              <input onChange={handleType} type="radio" className="option-input radio" value="offre" name="register" checked={types} />
              Offre
            </label>
            <label>
              <input onChange={handleType} type="radio" className="option-input radio" value="demande" name="register" checked={!types} />
              Demande
            </label>
          </div>
          <div>
            <select onChange={handleCategory} className="custom-select" id="inputGroupSelect01">
            <option>Choisir une catégorie</option>
            <optgroup label="Echanges de services">
            {services.map((category) => {
            return <option value={category.id}>{category.name}</option>
            })}
            </optgroup>
            <optgroup label="Pensions">
              {pensions.map((category) => {
              return <option value={category.id}>{category.name}</option>
            })}
            </optgroup>          
            <optgroup label="Activités et balades">
              {activites.map((category) => {
                return <option value={category.id}>{category.name}</option>
              })}
            </optgroup>
            <optgroup label="Ventes/Dons/Locations">
              {ventes.map((category) => {
                return <option value={category.id}>{category.name}</option>
              })}
            </optgroup>
            </select>
          </div>
          <div>
            <input onChange={handleKeyWord} placeholder="Rechercher par mots clés" type="text" className="form-control" aria-label="Default" aria-describedby="inputGroup-sizing-default" />
          </div>
          <div className="location">
            <button className="geoloc-btn" onClick={handleLocation}><FontAwesomeIcon icon={faCrosshairs} /> Localisez-moi!</button>
            <Autocomplete
              onChange={handleNewCities}
              className="autocomplete"
              autoSelect={true}
              id="free-solo-demo"
              freeSolo
              value={location}
              options={newCities}
              renderInput={params => (
                <TextField {...params} onChange={handleNewCities} label="Ville" margin="normal" variant="outlined" fullWidth />
              )}
            />
          </div>
        </form>
        <div className="middlePage">
          <div className="map">
          <CardMap 
          handleFilters={handleFilters}
          latitude={latitude}
          longitude={longitude} />
          </div>
          <div className="advertList">
            <AdvertsList handleFilters={handleFilters} />
          </div>   
        </div>
      </div>
    </Fragment>
  );
}

export default AdvertsPage;
