import React, { useState, useEffect, Fragment, useContext } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelope } from '@fortawesome/free-solid-svg-icons';
import Navbar from '../Navbar/Navbar';
import { CtxAdverts } from '../CtxAdverts';
import { apiEndPoint } from '../../config';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import '../css/Advert.css';

const Advert = (props) => {
  const [adverts, setAdverts] = useContext(CtxAdverts);
  const [img, setImg] = useState("");


  const handleMap = () => {
    return adverts.filter((advert) => {
      return parseInt(props.match.params.id) === advert.id
    })
      .map((advert) => {
        return (
          <Card id="advert-card">
            <CardActionArea>
              <CardContent>
                <Typography variant="body2" color="textSecondary" component="p">
                  Annonce nÂ°{advert.id}
                </Typography>
                <Typography gutterBottom variant="h5" component="h2">
                  {advert.title}
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  {advert.description}
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  {advert.location}
                </Typography>
                {advert.price > 0 ? 
                  <Typography variant="body2" color="textSecondary" component="p">
                    {advert.price} Euros
                  </Typography> : null}
                {advert.phone > 0 ? 
                  <Typography variant="body2" color="textSecondary" component="p">
                    Tel: {advert.phone}
                  </Typography> : null}
              </CardContent>
              <CardMedia
                component="img"
                alt="Contemplative Reptile"
                height="140"
                image={
                  advert.url_image !== '' ?
                  `${apiEndPoint}/uploads/${advert.url_image}` :
                  `${apiEndPoint}/uploads/default.jpg`
                  }
                title="Advert-image"
              />
            </CardActionArea>
            <CardActions>
              <a href={"mailto:" + advert.email}> <button className="btn btn-success m-1">Contacter {advert.name} <FontAwesomeIcon icon={faEnvelope} /></button></a>
              <button id="propose" type="button" className="btn btn-primary" onClick={print}> Imprimer</button>
            </CardActions>
          </Card>
        )
      })
  };

  const print = () => {
    return window.print();
  }

  // useEffect(() => {
  //   if (advertImage != "") {
  //     fetch(`${apiEndPoint}/image/${image}`)
  //       .then((response, err) => {
  //         if (err) {
  //           throw err;
  //         } else {
  //           setImg(response.url);
  //         }
  //       });
  //   } else {
  //     fetch(`${apiEndPoint}/image/default.jpg`)
  //       .then((response, err) => {
  //         if (err) {
  //           throw err;
  //         } else {
  //           setImg(response.url);
  //         }
  //       });
  //   }
  // }, []);

  return (
    <Fragment>
      <Navbar />
      <div id="advert">
        {adverts.length > 0
          ? handleMap()
          : <p>Loading...</p>
        }
      </div>
    </Fragment>
  );
}

export default Advert;
